import axios from 'axios';
import { useEffect, useState } from 'react';
import * as store from 'store';
import './sass/App.scss';

function App() {
  const [randomPokemon, setRandomPokemon] = useState(null);
  const [favoritePokemons, setFavoritePokemons] = useState(null);
  const [refreshScreen, setRefreshScreen] = useState(false);
  const pokemonBaseUrl = 'https://pokeapi.co/api/v2/pokemon/';
  const localStorageKey = 'favorites';

  // Max count from documentation
  // https://pokeapi.co/api/v2/pokemon
  const maxPokemonCount = 898;

  const getRandomPokemon = (e) => {
    e.preventDefault();

    const randomNumber = Math.floor(Math.random() * maxPokemonCount);

    const url = pokemonBaseUrl + randomNumber;

    axios.get(url).then((pokemon) => {
      setRandomPokemon({
        id: pokemon.data.id,
        name: pokemon.data.name,
        sprite: pokemon.data.sprites.front_default,
      });
    });
  };

  const saveToFav = (e) => {
    e.preventDefault();

    if (randomPokemon) {
      let temp = store.get(localStorageKey);

      if (temp != null) {
        if (!temp.some((pokemon) => pokemon.id === randomPokemon.id)) {
          temp.push(randomPokemon);
          store.set(localStorageKey, temp);
        }
      } else {
        temp = [];
        temp.push(randomPokemon);
        store.set(localStorageKey, temp);
      }

      // Screen refresher because favorites are added on localstorage,
      // it doesn't cause the component to reload so force reload it
      setRefreshScreen(!refreshScreen);
    }
  };

  useEffect(() => {
    setFavoritePokemons(store.get(localStorageKey));
  }, [refreshScreen]);

  const clearFavs = () => {
    store.clearAll();

    // Screen refresher because favorites are added on localstorage,
    // it doesn't cause the component to reload so force reload it
    setRefreshScreen(!refreshScreen);
  };

  return (
    <div>
      <h1>Pokedex</h1>
      <header class="header header--blog">
	<div class="header__text-box">
		<h1 class="heading-primary heading-primary--blog">
			You've signed up
		</h1>
		<p class="heading-text">
			There's nothing left to do.
		</p>
	</div>
</header>
      <button onClick={getRandomPokemon}>Get Random Pokemon</button>
      {randomPokemon && (
        <div>
          <img src={randomPokemon.sprite} alt={randomPokemon.name} />
          <h2>{randomPokemon.name}</h2>
          <button onClick={saveToFav}>Add to Favorites</button>
        </div>
      )}
      <br />
      <h2>Favorites</h2>
      {favoritePokemons &&
        favoritePokemons.map((pokemon) => (
          <h4 key={pokemon.id}>{pokemon.name}</h4>
        ))}
      <button onClick={clearFavs}>Clear Favorites</button>
    </div>
  );
}

export default App;
