import axios from 'axios';
import { useEffect, useState } from 'react';
import * as store from 'store';

function App() {
  const [randomPokemon, setRandomPokemon] = useState(null);
  const [favoritePokemons, setFavoritePokemons] = useState(null);
  const [refreshScreen, setRefreshScreen] = useState(false);
  const pokemonBaseUrl = 'https://pokeapi.co/api/v2/pokemon/';
  const localStorageKey = 'favorites';

  // Max count from documentation
  // https://pokeapi.co/api/v2/pokemon
  const maxPokemonCount = 898;

  const getRandomPokemon = (e) => {
    e.preventDefault();

    const randomNumber = Math.floor(Math.random() * maxPokemonCount);

    const url = pokemonBaseUrl + randomNumber;

    axios.get(url).then((pokemon) => {
      setRandomPokemon({
        id: pokemon.data.id,
        name: pokemon.data.name,
        sprite: pokemon.data.sprites.front_default,
      });
    });
  };

  const saveToFav = (e) => {
    e.preventDefault();

    if (randomPokemon) {
      let temp = store.get(localStorageKey);

      if (temp != null) {
        if (!temp.some((pokemon) => pokemon.id === randomPokemon.id)) {
          temp.push(randomPokemon);
          store.set(localStorageKey, temp);
        }
      } else {
        temp = [];
        temp.push(randomPokemon);
        store.set(localStorageKey, temp);
      }

      // Screen refresher because favorites are added on localstorage,
      // it doesn't cause the component to reload so force reload it
      setRefreshScreen(!refreshScreen);
    }
  };

  useEffect(() => {
    setFavoritePokemons(store.get(localStorageKey));
  }, [refreshScreen]);

  const clearFavs = () => {
    store.clearAll();

    // Screen refresher because favorites are added on localstorage,
    // it doesn't cause the component to reload so force reload it
    setRefreshScreen(!refreshScreen);
  };

  return (
    <div>
      <h1>Pokedex</h1>
      <main id="main">
        <article className="post">
          <h2 className="u-margin-bottom-none">
            <a href="/blog/attention/" className="post__title">
              Find a favourite pokemon
            </a>
          </h2>
          <p className="post__excerpt">
            Did you know there 898 pokemon? Click the button below to discover new pokemon!
          </p>
          <button className="btn btn--reverse  contact__btn" onClick={getRandomPokemon}>Get Random Pokemon</button>
        </article>
        <section>
          {randomPokemon && (
            <div className="profile">
              <div className="profile__container">
                <figure className="profile__shape">
                  <picture>
                    <img alt={randomPokemon.name} loading="lazy" src={randomPokemon.sprite} width="200" height="150"></img>
                  </picture>
                </figure>
                <div className="profile__text">
                  <h2 className="heading-tertiary u-margin-bottom-small">{randomPokemon.name}</h2>
                  <button className="btn btn--reverse  contact__btn"  onClick={saveToFav}>Add to Favorites</button>
                </div>
              </div>
            </div>
          )}
        </section>
        <article class="post">
            <h2 className="u-margin-bottom-none">
              <a href="/blog/attention/" className="post__title">
                Favourites
              </a>
            </h2>
        {favoritePokemons &&
          favoritePokemons.map((pokemon) => (
            <article className="post">

              <div className="profile">
                <div className="profile__container">
                  <figure className="profile__shape">
                    <picture>
                      <img key={pokemon.id} alt={pokemon.name} loading="lazy" src={pokemon.sprite}></img>
                    </picture>
                  </figure>
                  <div className="profile__text">
                    <h2 className="heading-tertiary u-margin-bottom-small">{pokemon.name}</h2>
                  </div>
                </div>
              </div>
            </article>
          ))}
          </article>
      </main>
    </div>
  );
}

export default App;
