import axios from 'axios';
import { useEffect, useState } from 'react';
import * as store from 'store';

function App() {
  const [randomPokemon, setRandomPokemon] = useState(null);
  const [favoritePokemons, setFavoritePokemons] = useState(null);
  const [refreshScreen, setRefreshScreen] = useState(false);
  const pokemonBaseUrl = 'https://pokeapi.co/api/v2/pokemon/';
  const localStorageKey = 'favorites';

  // Max count from documentation
  // https://pokeapi.co/api/v2/pokemon
  const maxPokemonCount = 898;

  const getRandomPokemon = (e) => {
    e.preventDefault();

    const randomNumber = Math.floor(Math.random() * maxPokemonCount);

    const url = pokemonBaseUrl + randomNumber;

    axios.get(url).then((pokemon) => {
      setRandomPokemon({
        id: pokemon.data.id,
        name: pokemon.data.name,
        sprite: pokemon.data.sprites.front_default,
      });
    });
  };

  const saveToFav = (e) => {
    e.preventDefault();

    if (randomPokemon) {
      let temp = store.get(localStorageKey);

      if (temp != null) {
        if (!temp.some((pokemon) => pokemon.id === randomPokemon.id)) {
          temp.push(randomPokemon);
          store.set(localStorageKey, temp);
        }
      } else {
        temp = [];
        temp.push(randomPokemon);
        store.set(localStorageKey, temp);
      }

      // Screen refresher because favorites are added on localstorage,
      // it doesn't cause the component to reload so force reload it
      setRefreshScreen(!refreshScreen);
    }
  };

  useEffect(() => {
    setFavoritePokemons(store.get(localStorageKey));
  }, [refreshScreen]);

  const clearFavs = () => {
    store.clearAll();

    // Screen refresher because favorites are added on localstorage,
    // it doesn't cause the component to reload so force reload it
    setRefreshScreen(!refreshScreen);
  };

  return (
    <div>
      <h1>Pokedex</h1>
      <main id="main">
        <article class="post">
          <h2 class="u-margin-bottom-none">
            <a href="/blog/attention/" class="post__title">
              Find a favourite pokemon
            </a>
          </h2>
          <p class="post__excerpt">
            Did you know there 898 pokemon? Click the button below to discover new pokemon!
          </p>
          <button class="btn btn--reverse  contact__btn" onClick={getRandomPokemon}>Get Random Pokemon</button>
        </article>
        <section class="section-about">
          <div class="profile">
            {randomPokemon && (
                <div class="profile__container">
                  <figure class="profile__shape">
                    <picture>
                      <img src={randomPokemon.sprite} alt={randomPokemon.name} />
                      <img alt="Adrian Yadav" loading="lazy" src="/img/uploads/adrianyadav.jpg" width="150" height="150"></img>
                    </picture>
                  </figure>
                  <div class="profile__text">
                    <h2 class="heading-tertiary u-margin-bottom-small">{randomPokemon.name}</h2>
                  </div>
                </div>
            )}
          </div>
        </section>
        <h2>Favorites</h2>
        {favoritePokemons &&
          favoritePokemons.map((pokemon) => (
            <h4 key={pokemon.id}>{pokemon.name}</h4>
          ))}
        <button class="btn btn--reverse contact__btn" onClick={clearFavs}>Clear Favorites</button>
      </main>
    </div>
  );
}

export default App;
